import Card from "../UI/Card";
import classes from "./AvailableMeals.module.css";
import MealItem from "./MealItem/MealItem";
let DUMMY_MEALS=[] ;
description: "Finest fish and veggies"
id: "m1"
name: "Sushi"
price: 22.99

const AvailableMeals = () => {
    fetch('https://taskmanager-87909-default-rtdb.firebaseio.com/Meals.json').then(Response=>Response.json())
    .then(data=>{
      for (const taskKey in data) {
        DUMMY_MEALS.push({ id: data[taskKey].id, description: data[taskKey].description,
        price:data[taskKey].price,
      name:data[taskKey].name });
      }
    })

  const meals = DUMMY_MEALS.map((meal) => (
    <MealItem meal={meal} key={meal.id} />
  ));



  return (
    <section className={classes.meals}>
      <Card>
        <ul>{meals}</ul>
      </Card>
    </section>
  );
};
export default AvailableMeals;
