import { useReducer } from "react";
import CartContext from "./cart-context.js";

const defaultState = {
  items: [],
  total: 0,
};

// we can also define it inside component() but as we don't use any of its thing here so it will cause unnecessary
// re-rendering of this function
const cartReducer = (state, action) => {
  if (action.type === "ADD_ITEM") {
    return {
      items: state.items.concat(action.item),
      total: state.total+action.item.price*action.item.amount,
    };}

    else if(action.type==='APPEND_ITEM')
    {
      const updatedItems=state.items.map(item=>{
        if(item.id===action.id)
        item.amount+=1;
        return item;
      })
    }
  
  return defaultState;
};

function CartProvider(props) {
  //we are not using 2 useState hooks here as we have 2 dependent and complex states going to use in removeitem()
  const [cartState, dispatchCartAction] = useReducer(cartReducer, defaultState);

  const addItemHandler = (item) => {
    cartState.items.forEach(itm => {
      if(item.id===itm.id)
    dispatchCartAction({ type: "APPEND_ITEM", id:item.id });
    });

    dispatchCartAction({ type: "ADD_ITEM", item: item });
  };

  const removeItemHandler = (id) => {
    dispatchCartAction({ type: "REMOVE_ITEM", id:id });
  };
  const cartContextValue = {
    items: cartState.items,
    total: cartState.total,
    addItem: addItemHandler,
    removeItem: removeItemHandler,
  };

  return (
    <CartContext.Provider value={cartContextValue}>
      {props.children}
    </CartContext.Provider>
  );
}
export default CartProvider;
