import { useRef, useState } from "react";
import classes from "./checkout.module.css";

function isEmpty(data){
  return data.trim()!=='';
}

function Checkout(props) {
  const nameRef=useRef();
  const postalCodeRef=useRef();
  const cityRef=useRef();
  const streetRef=useRef();

  const [formValidity,setFormValidity]=useState({
   nameIsValid:true,
   streetIsValid:true,
   cityIsValid:true,
   codeIsValid:true
  });

    function submitHandler(event) {
    const enteredName=nameRef.current.value;
    const enteredStreet=streetRef.current.value;
    const enteredCity=cityRef.current.value;
    const enteredCode =postalCodeRef.current.value;

    

      
    event.preventDefault();
  }
  return (
    <form className={classes.form} onSubmit={submitHandler}>
      <div className={classes.control}>
        <label htmlFor="name">Your Name</label>
        <input type="text" id="name" ref={nameRef}></input>
      </div>
      <div className={classes.control}>
        <label htmlFor="Street">Street</label>
        <input type="text" id="Street" ref={streetRef}></input>
      </div>
      <div className={classes.control}>
        <label htmlFor="code">Postal Code</label>
        <input type="text" id="code" ref={postalCodeRef}></input>
      </div>
      <div className={classes.control}>
        <label htmlFor='city'>City</label>
        <input type='text' id='city' ref={cityRef} />
      </div>
      <div className={classes.actions}>
        <button type='button' onClick={props.onHide}>Cancel</button>
        <button className={classes.submit} type="submit">Confirm</button>
      </div>
    </form>
  );
}
export default Checkout;
